import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App';
import { transactionAPI } from '../services/api';

const WithdrawModal = () => {
  const { user, updateUser } = React.useContext(UserContext);
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({
    amount: '',
    description: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.amount.trim()) {
      newErrors.amount = 'Amount is required';
    } else {
      const amount = parseFloat(formData.amount);
      const currentBalance = parseFloat(user?.balance || 0);
      
      if (isNaN(amount) || amount <= 0) {
        newErrors.amount = 'Please enter a valid amount greater than 0';
      } else if (amount > currentBalance) {
        newErrors.amount = `Insufficient balance. Available: $${currentBalance.toFixed(2)}`;
      }
      if (amount > 5000) {
        newErrors.amount = 'Maximum withdrawal amount is $5,000';
      }
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    
    try {
      const amount = parseFloat(formData.amount);
      const transaction = await transactionAPI.withdraw(
        user.id,
        amount,
        formData.description || 'Money withdrawn'
      );
      
      // Update user balance
      const updatedUser = { ...user, balance: transaction.balanceAfterTransaction };
      updateUser(updatedUser);
      
      // Navigate back to dashboard
      navigate('/dashboard');
    } catch (error) {
      setErrors({
        general: error.error || 'Withdrawal failed. Please try again.'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    navigate('/dashboard');
  };

  const getQuickAmounts = () => {
    const currentBalance = parseFloat(user?.balance || 0);
    const amounts = [];
    
    if (currentBalance >= 50) amounts.push(50);
    if (currentBalance >= 100) amounts.push(100);
    if (currentBalance >= 250) amounts.push(250);
    if (currentBalance >= 500) amounts.push(500);
    if (currentBalance >= 1000) amounts.push(1000);
    
    return amounts;
  };

  const handlePresetAmount = (amount) => {
    setFormData(prev => ({
      ...prev,
      amount: amount.toString()
    }));
    setErrors(prev => ({
      ...prev,
      amount: ''
    }));
  };

  const handleWithdrawAll = () => {
    const currentBalance = parseFloat(user?.balance || 0);
    if (currentBalance > 0) {
      setFormData(prev => ({
        ...prev,
        amount: currentBalance.toFixed(2)
      }));
      setErrors(prev => ({
        ...prev,
        amount: ''
      }));
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2 className="modal-title">Withdraw Money</h2>
          <button onClick={handleClose} className="close-btn">Ã—</button>
        </div>

        <form onSubmit={handleSubmit}>
          {errors.general && (
            <div className="error-message mb-3 text-center">
              {errors.general}
            </div>
          )}

          <div className="card mb-3">
            <div className="balance-card">
              <div className="balance-label">Available Balance</div>
              <div className="balance-amount">
                ${(user?.balance || 0).toFixed(2)}
              </div>
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="amount" className="form-label">
              Amount to Withdraw
            </label>
            <input
              type="number"
              id="amount"
              name="amount"
              value={formData.amount}
              onChange={handleChange}
              className={`form-input ${errors.amount ? 'error' : ''}`}
              placeholder="0.00"
              step="0.01"
              min="0.01"
              max={user?.balance || 0}
            />
            {errors.amount && (
              <div className="error-message">{errors.amount}</div>
            )}
          </div>

          <div className="form-group">
            <label className="form-label">Quick Amounts</label>
            <div className="quick-actions">
              {getQuickAmounts().map((amount) => (
                <button
                  key={amount}
                  type="button"
                  className="quick-action-btn"
                  onClick={() => handlePresetAmount(amount)}
                >
                  ${amount}
                </button>
              ))}
              {parseFloat(user?.balance || 0) > 0 && (
                <button
                  type="button"
                  className="quick-action-btn"
                  onClick={handleWithdrawAll}
                >
                  All
                </button>
              )}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="description" className="form-label">
              Description (Optional)
            </label>
            <input
              type="text"
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter a description for this withdrawal"
            />
          </div>

          <div className="flex justify-between mt-4">
            <button
              type="button"
              onClick={handleClose}
              className="btn btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-danger"
              disabled={loading}
            >
              {loading ? (
                <>
                  <div className="spinner" style={{ width: '20px', height: '20px' }}></div>
                  Processing...
                </>
              ) : (
                'Withdraw Money'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default WithdrawModal;
