import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App';
import { transactionAPI, customerAPI } from '../services/api';

const TransferModal = () => {
  const { user, updateUser } = React.useContext(UserContext);
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({ 
    toUsername: '', 
    amount: '', 
    description: '' 
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [currentBalance, setCurrentBalance] = useState(user?.balance || 0);
  const [recipientInfo, setRecipientInfo] = useState(null);
  const [searchingRecipient, setSearchingRecipient] = useState(false);

  // Fetch current balance when component mounts
  useEffect(() => {
    const fetchCurrentBalance = async () => {
      if (user?.id) {
        try {
          const balanceData = await customerAPI.getBalance(user.id);
          setCurrentBalance(balanceData.balance);
          console.log('TransferModal: Fetched current balance:', balanceData.balance);
        } catch (error) {
          console.error('TransferModal: Error fetching balance:', error);
          setCurrentBalance(user.balance || 0);
        }
      }
    };
    
    fetchCurrentBalance();
  }, [user?.id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) setErrors(prev => ({ ...prev, [name]: '' }));
    
    // Clear recipient info when username changes
    if (name === 'toUsername') {
      setRecipientInfo(null);
    }
  };

  const searchRecipient = async () => {
    if (!formData.toUsername.trim()) {
      setErrors(prev => ({ ...prev, toUsername: 'Please enter a username' }));
      return;
    }

    if (formData.toUsername.trim() === user?.username) {
      setErrors(prev => ({ ...prev, toUsername: 'Cannot transfer to your own account' }));
      return;
    }

    setSearchingRecipient(true);
    setErrors(prev => ({ ...prev, toUsername: '' }));

    try {
      // We'll use a simple approach - try to get customer by username
      // This will throw an error if username doesn't exist
      const response = await fetch(`http://localhost:8080/api/customers/username/${formData.toUsername.trim()}`);
      
      if (response.ok) {
        const customerData = await response.json();
        setRecipientInfo({
          id: customerData.id,
          username: customerData.username,
          firstName: customerData.firstName,
          lastName: customerData.lastName,
          email: customerData.email
        });
        console.log('TransferModal: Found recipient:', customerData);
      } else {
        setErrors(prev => ({ ...prev, toUsername: 'Username not found' }));
        setRecipientInfo(null);
      }
    } catch (error) {
      console.error('TransferModal: Error searching recipient:', error);
      setErrors(prev => ({ ...prev, toUsername: 'Error searching for username' }));
      setRecipientInfo(null);
    } finally {
      setSearchingRecipient(false);
    }
  };

  const validateForm = () => {
    const newErrors = {};
    const amt = parseFloat(formData.amount);
    const balance = parseFloat(currentBalance || 0);
    
    if (!formData.toUsername.trim()) {
      newErrors.toUsername = 'Please enter recipient username';
    } else if (formData.toUsername.trim() === user?.username) {
      newErrors.toUsername = 'Cannot transfer to your own account';
    } else if (!recipientInfo) {
      newErrors.toUsername = 'Please search and verify the recipient first';
    }
    
    if (isNaN(amt) || amt <= 0) {
      newErrors.amount = 'Enter a valid amount > 0';
    } else if (amt > balance) {
      newErrors.amount = `Insufficient balance. Available: $${balance.toFixed(2)}`;
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    
    setLoading(true);
    try {
      const amt = parseFloat(formData.amount);
      const transferResult = await transactionAPI.transfer(
        user.id, 
        formData.toUsername.trim(), 
        amt, 
        formData.description || 'Money transfer'
      );
      
      console.log('TransferModal: Transfer successful:', transferResult);
      
      // Update user balance
      updateUser({ ...user, balance: transferResult.fromCustomerNewBalance });
      
      // Show success message
      alert(`Transfer successful! $${amt.toFixed(2)} sent to ${recipientInfo.firstName} ${recipientInfo.lastName} (${recipientInfo.username})`);
      
      // Navigate back to dashboard
      navigate('/dashboard');
    } catch (error) {
      console.error('TransferModal: Transfer error:', error);
      setErrors({ general: error.error || 'Transfer failed. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => navigate('/dashboard');

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2 className="modal-title">Transfer Money</h2>
          <div className="flex gap-2">
            <button 
              onClick={async () => {
                try {
                  const balanceData = await customerAPI.getBalance(user.id);
                  setCurrentBalance(balanceData.balance);
                  console.log('TransferModal: Refreshed balance:', balanceData.balance);
                } catch (error) {
                  console.error('TransferModal: Error refreshing balance:', error);
                }
              }}
              className="btn btn-secondary"
              type="button"
            >
              Refresh Balance
            </button>
            <button onClick={handleClose} className="close-btn">Ã—</button>
          </div>
        </div>
        
        <form onSubmit={handleSubmit}>
          {errors.general && <div className="error-message mb-3 text-center">{errors.general}</div>}
          
          {/* Current Balance Display */}
          <div className="form-group">
            <label className="form-label">Your Current Balance</label>
            <div className="balance-display">
              ${parseFloat(currentBalance || 0).toFixed(2)}
            </div>
          </div>
          
          {/* Recipient Username */}
          <div className="form-group">
            <label className="form-label">Recipient Username</label>
            <div className="flex gap-2">
              <input 
                type="text" 
                name="toUsername" 
                value={formData.toUsername} 
                onChange={handleChange} 
                className={`form-input flex-1 ${errors.toUsername ? 'error' : ''}`} 
                placeholder="Enter recipient's username"
                disabled={loading}
              />
              <button 
                type="button"
                onClick={searchRecipient}
                className="btn btn-secondary"
                disabled={searchingRecipient || loading}
              >
                {searchingRecipient ? 'Searching...' : 'Search'}
              </button>
            </div>
            {errors.toUsername && <div className="error-message">{errors.toUsername}</div>}
          </div>
          
          {/* Recipient Info Display */}
          {recipientInfo && (
            <div className="form-group">
              <div className="recipient-info">
                <h4>Recipient Details:</h4>
                <p><strong>Name:</strong> {recipientInfo.firstName} {recipientInfo.lastName}</p>
                <p><strong>Username:</strong> {recipientInfo.username}</p>
                <p><strong>Email:</strong> {recipientInfo.email}</p>
              </div>
            </div>
          )}
          
          {/* Amount */}
          <div className="form-group">
            <label className="form-label">Amount</label>
            <input 
              type="number" 
              name="amount" 
              value={formData.amount} 
              onChange={handleChange} 
              className={`form-input ${errors.amount ? 'error' : ''}`} 
              step="0.01" 
              min="0.01" 
              placeholder="0.00"
              disabled={loading}
            />
            {errors.amount && <div className="error-message">{errors.amount}</div>}
          </div>
          
          {/* Description */}
          <div className="form-group">
            <label className="form-label">Description (optional)</label>
            <input 
              name="description" 
              value={formData.description} 
              onChange={handleChange} 
              className="form-input" 
              placeholder="Enter description for this transfer"
              disabled={loading}
            />
          </div>
          
          <div className="flex justify-between mt-4">
            <button 
              type="button" 
              onClick={handleClose} 
              className="btn btn-secondary"
              disabled={loading}
            >
              Cancel
            </button>
            <button 
              type="submit" 
              className="btn btn-primary" 
              disabled={loading || !recipientInfo}
            >
              {loading ? 'Processing...' : 'Transfer Money'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TransferModal;
