import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App';
import { transactionAPI } from '../services/api';

const Passbook = () => {
  const { user } = React.useContext(UserContext);
  const navigate = useNavigate();

  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('all');
  const [debugInfo, setDebugInfo] = useState(null); // Add debug state

  useEffect(() => {
    const fetchData = async () => {
      if (!user) {
        console.log('DEBUG: No user found');
        setDebugInfo('No user found');
        return;
      }
      
      console.log('DEBUG: User ID:', user.id);
      setLoading(true);
      setError(null);
      
      try {
        console.log('DEBUG: Calling transactionAPI.getPassbook with user ID:', user.id);
        const passbook = await transactionAPI.getPassbook(user.id);
        
        console.log('DEBUG: Raw passbook response:', passbook);
        console.log('DEBUG: Passbook transactions:', passbook.transactions);
        console.log('DEBUG: Transactions length:', passbook.transactions?.length || 0);
        
        // More detailed debugging
        if (passbook.transactions) {
          passbook.transactions.forEach((t, index) => {
            console.log(`DEBUG: Transaction ${index}:`, {
              id: t.id,
              type: t.type,
              amount: t.amount,
              description: t.description,
              timestamp: t.timestamp
            });
          });
        }
        
        let safeTransactions = Array.isArray(passbook.transactions) ? passbook.transactions : [];
        
        // If passbook returns empty, fall back to standard transactions endpoint
        if (!safeTransactions || safeTransactions.length === 0) {
          console.log('DEBUG: Passbook returned empty. Falling back to getTransactions');
          try {
            const tx = await transactionAPI.getTransactions(user.id);
            console.log('DEBUG: Fallback getTransactions result:', tx);
            safeTransactions = Array.isArray(tx) ? tx : [];
          } catch (fallbackErr) {
            console.error('DEBUG: Fallback getTransactions failed:', fallbackErr);
          }
        }
        
        // Sort by timestamp desc if available
        const sorted = [...safeTransactions].sort((a, b) => {
          const ta = a?.timestamp ? new Date(a.timestamp).getTime() : 0;
          const tb = b?.timestamp ? new Date(b.timestamp).getTime() : 0;
          return tb - ta;
        });
        setTransactions(sorted);
        setDebugInfo(`Loaded ${safeTransactions.length} transactions (after fallback if applied)`);
        
      } catch (err) {
        console.error('DEBUG: Error fetching transactions:', err);
        console.log('DEBUG: Error details:', {
          error: err.error,
          message: err.message,
          stack: err.stack,
          response: err.response
        });
        
        setError(err.error || err.message || 'Failed to load transactions');
        setDebugInfo(`Error: ${err.error || err.message}`);
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, [user]);

  // Debug filtered transactions
  const filtered = React.useMemo(() => {
    console.log('DEBUG: Computing filtered transactions...');
    console.log('DEBUG: All transactions:', transactions);
    console.log('DEBUG: Current filter:', filter);
    
    const result = transactions.filter(t => {
      if (!t) {
        console.log('DEBUG: Found null/undefined transaction');
        return false;
      }
      
      const matchesFilter = filter === 'all' || (t.type && t.type.toLowerCase() === filter);
      console.log(`DEBUG: Transaction ${t.id} (${t.type}) matches filter '${filter}':`, matchesFilter);
      return matchesFilter;
    });
    
    console.log('DEBUG: Filtered result:', result);
    console.log('DEBUG: Filtered transactions count:', result.length);
    return result;
  }, [transactions, filter]);

  const formatCurrency = (amount) => new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(amount);
  const formatDate = (date) => new Date(date).toLocaleString();

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading transactions...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card">
        <div className="error-message text-center">
          <h3>Error Loading Transactions</h3>
          <p>{error}</p>
          <p><strong>Debug Info:</strong> {debugInfo}</p>
          <button className="btn btn-primary mt-2" onClick={() => navigate('/dashboard')}>Back</button>
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Debug Panel - Remove this in production */}
      <div className="card" style={{backgroundColor: '#f0f0f0', border: '2px solid #orange'}}>
        <div className="card-header">
          <h3>Debug Information</h3>
        </div>
        <div style={{padding: '10px'}}>
          <p><strong>User ID:</strong> {user?.id || 'No user'}</p>
          <p><strong>Total Transactions:</strong> {transactions.length}</p>
          <p><strong>Filtered Transactions:</strong> {filtered.length}</p>
          <p><strong>Current Filter:</strong> {filter}</p>
          <p><strong>Debug Info:</strong> {debugInfo}</p>
          <details>
            <summary>Raw Transaction Data</summary>
            <pre style={{fontSize: '12px', overflow: 'auto'}}>
              {JSON.stringify(transactions, null, 2)}
            </pre>
          </details>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <div>
            <h1 className="card-title">Transaction Passbook</h1>
            <p className="card-subtitle">Full transaction history</p>
          </div>
          <button className="btn btn-secondary" onClick={() => navigate('/dashboard')}>Back to Dashboard</button>
        </div>
      </div>

      <div className="card">
        <div className="flex gap-2">
          <button className={`btn ${filter === 'all' ? 'btn-primary' : 'btn-secondary'}`} onClick={() => setFilter('all')}>All ({transactions.length})</button>
          <button className={`btn ${filter === 'deposit' ? 'btn-primary' : 'btn-secondary'}`} onClick={() => setFilter('deposit')}>
            Deposits ({transactions.filter(t => t && t.type && t.type.toLowerCase() === 'deposit').length})
          </button>
          <button className={`btn ${filter === 'withdrawal' ? 'btn-primary' : 'btn-secondary'}`} onClick={() => setFilter('withdrawal')}>
            Withdrawals ({transactions.filter(t => t && t.type && t.type.toLowerCase() === 'withdrawal').length})
          </button>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Transactions</h2>
        </div>
{/* Force render check */}
        <div style={{padding: '10px', backgroundColor: '#ffffcc', border: '1px solid #orange'}}>
          <strong>Render Check:</strong> This should always show. 
          Filtered length: {filtered.length}, 
          Transactions length: {transactions.length}
        </div>
        
        {filtered.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">📭</div>
            <div className="empty-state-title">No Transactions Found</div>
            <div className="empty-state-description">
              {transactions.length === 0 
                ? 'No transactions in database. Try making a deposit or withdrawal.'
                : `No transactions match filter "${filter}". Try selecting "All" or a different filter.`
              }
            </div>
          </div>
        ) : (
          <>
            <div style={{padding: '10px', backgroundColor: '#ccffcc'}}>
              <strong>About to render {filtered.length} transactions</strong>
            </div>
            <div className="transaction-list">
              {filtered.map((t, index) => {
                console.log(`DEBUG: Rendering transaction ${index}:`, t);
                
                // Add safety checks
                if (!t || !t.id) {
                  console.log('DEBUG: Skipping invalid transaction:', t);
                  return null;
                }
                
                return (
                  <div key={t.id} className="transaction-item" style={{border: '1px solid #ccc', margin: '5px', padding: '10px'}}>
                    <div className="transaction-info">
                      <div className={`transaction-type ${t.type?.toLowerCase() || 'unknown'}`}>
                        {t.type === 'DEPOSIT' ? 'Deposit' : t.type === 'WITHDRAWAL' ? 'Withdrawal' : t.type || 'Unknown'}
                      </div>
                      <div className="transaction-description">{t.description || 'No description'}</div>
                      <div className="transaction-date">
                        {t.timestamp ? formatDate(t.timestamp) : 'No date'}
                      </div>
                      <div className="text-xs text-gray-500 mt-1">
                        Balance after: {t.balanceAfterTransaction ? formatCurrency(t.balanceAfterTransaction) : 'N/A'}
                      </div>
                    </div>
                    <div className={`transaction-amount ${t.type === 'DEPOSIT' ? 'positive' : 'negative'}`}>
                      {t.type === 'DEPOSIT' ? '+' : '-'}{t.amount ? formatCurrency(t.amount) : '$0.00'}
                    </div>
                  </div>
                );
              })}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Passbook;
