import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App';
import { transactionAPI } from '../services/api';

const Passbook = () => {
  const { user } = React.useContext(UserContext);
  const navigate = useNavigate();
  
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('all'); // all, deposit, withdrawal

  useEffect(() => {
    fetchTransactions();
  }, [user]);

  const fetchTransactions = async () => {
    if (!user) return;
    
    setLoading(true);
    setError(null);
    
    try {
      const passbookData = await transactionAPI.getPassbook(user.id);
      setTransactions(passbookData.transactions || []);
    } catch (error) {
      setError(error.error || 'Failed to load transactions');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatDateShort = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric'
    });
  };

  const getFilteredTransactions = () => {
    if (filter === 'all') {
      return transactions;
    }
    return transactions.filter(transaction => 
      transaction.type.toLowerCase() === filter
    );
  };

  const getTransactionSummary = () => {
    const totalDeposits = transactions
      .filter(t => t.type === 'DEPOSIT')
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);
    
    const totalWithdrawals = transactions
      .filter(t => t.type === 'WITHDRAWAL')
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);
    
    return {
      totalDeposits,
      totalWithdrawals,
      netAmount: totalDeposits - totalWithdrawals
    };
  };

  const summary = getTransactionSummary();
  const filteredTransactions = getFilteredTransactions();

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card">
        <div className="error-message text-center">
          {error}
          <button 
            onClick={fetchTransactions} 
            className="btn btn-primary mt-2"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Header */}
      <div className="card">
        <div className="card-header">
          <div>
            <h1 className="card-title">Transaction Passbook</h1>
            <p className="card-subtitle">
              Complete transaction history for {user?.firstName} {user?.lastName}
            </p>
          </div>
          <button
            className="btn btn-secondary"
            onClick={() => navigate('/dashboard')}
          >
            Back to Dashboard
          </button>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-3">
        <div className="card">
          <div className="text-center">
            <h3 className="card-title text-success">Total Deposits</h3>
            <div className="balance-amount text-success">
              {formatCurrency(summary.totalDeposits)}
            </div>
          </div>
        </div>
        
        <div className="card">
          <div className="text-center">
            <h3 className="card-title text-danger">Total Withdrawals</h3>
            <div className="balance-amount text-danger">
              {formatCurrency(summary.totalWithdrawals)}
            </div>
          </div>
        </div>
        
        <div className="card">
          <div className="text-center">
            <h3 className="card-title">Net Amount</h3>
            <div className={`balance-amount ${summary.netAmount >= 0 ? 'text-success' : 'text-danger'}`}>
              {formatCurrency(summary.netAmount)}
            </div>
          </div>
        </div>
      </div>

      {/* Filter and Actions */}
      <div className="card">
        <div className="flex justify-between items-center">
          <div className="flex gap-2">
            <button
              className={`btn ${filter === 'all' ? 'btn-primary' : 'btn-secondary'}`}
              onClick={() => setFilter('all')}
            >
              All Transactions ({transactions.length})
            </button>
            <button
              className={`btn ${filter === 'deposit' ? 'btn-primary' : 'btn-secondary'}`}
              onClick={() => setFilter('deposit')}
            >
              Deposits ({transactions.filter(t => t.type === 'DEPOSIT').length})
            </button>
            <button
              className={`btn ${filter === 'withdrawal' ? 'btn-primary' : 'btn-secondary'}`}
              onClick={() => setFilter('withdrawal')}
            >
              Withdrawals ({transactions.filter(t => t.type === 'WITHDRAWAL').length})
            </button>
          </div>
          
          <div className="flex gap-2">
            <button
              className="btn btn-success"
              onClick={() => navigate('/deposit')}
            >
              New Deposit
            </button>
            <button
              className="btn btn-danger"
              onClick={() => navigate('/withdraw')}
            >
              New Withdrawal
            </button>
          </div>
        </div>
      </div>

      {/* Transactions List */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Transaction History</h2>
        </div>
        
        {filteredTransactions.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">
              {filter === 'all' ? 'ðŸ“Š' : filter === 'deposit' ? 'ðŸ’°' : 'ðŸ’¸'}
            </div>
            <div className="empty-state-title">
              {filter === 'all' ? 'No Transactions Yet' : 
               filter === 'deposit' ? 'No Deposits Yet' : 'No Withdrawals Yet'}
            </div>
            <div className="empty-state-description">
              {filter === 'all' ? 'Start by making your first transaction' :
               filter === 'deposit' ? 'Make your first deposit to get started' :
               'Make your first withdrawal'}
            </div>
            <button
              className="btn btn-primary mt-3"
              onClick={() => navigate(filter === 'withdrawal' ? '/withdraw' : '/deposit')}
            >
              {filter === 'withdrawal' ? 'Make Withdrawal' : 'Make Deposit'}
            </button>
          </div>
        ) : (
          <div className="transaction-list">
            {filteredTransactions.map((transaction, index) => (
              <div key={transaction.id} className="transaction-item">
                <div className="flex items-center gap-3">
                  <div className="text-center">
                    <div className="text-xs text-gray-500">
                      {formatDateShort(transaction.timestamp)}
                    </div>
                    <div className="text-lg">
                      {transaction.type === 'DEPOSIT' ? 'ðŸ’°' : 'ðŸ’¸'}
                    </div>
                  </div>
                  
                  <div className="transaction-info flex-1">
                    <div className={`transaction-type ${transaction.type.toLowerCase()}`}>
                      {transaction.type === 'DEPOSIT' ? 'Deposit' : 'Withdrawal'}
                    </div>
                    <div className="transaction-description">
                      {transaction.description || 'No description provided'}
                    </div>
                    <div className="transaction-date">
                      {formatDate(transaction.timestamp)}
                    </div>
                    <div className="text-xs text-gray-500 mt-1">
                      Balance after: {formatCurrency(transaction.balanceAfterTransaction)}
                    </div>
                  </div>
                  
                  <div className="text-right">
                    <div className={`transaction-amount ${
                      transaction.type === 'DEPOSIT' ? 'positive' : 'negative'
                    }`}>
                      {transaction.type === 'DEPOSIT' ? '+' : '-'}
                      {formatCurrency(transaction.amount)}
                    </div>
                    <div className="text-xs text-gray-500">
                      #{transaction.id}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Export/Print Options */}
      <div className="card">
        <div className="text-center">
          <h3 className="card-title mb-3">Export Options</h3>
          <div className="flex justify-center gap-2">
            <button
              className="btn btn-secondary"
              onClick={() => window.print()}
            >
              Print Passbook
            </button>
            <button
              className="btn btn-secondary"
              onClick={() => {
                const dataStr = JSON.stringify(transactions, null, 2);
                const dataBlob = new Blob([dataStr], {type: 'application/json'});
                const url = URL.createObjectURL(dataBlob);
                const link = document.createElement('a');
                link.href = url;
                link.download = `passbook_${user?.username}_${new Date().toISOString().split('T')[0]}.json`;
                link.click();
              }}
            >
              Export as JSON
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Passbook;
