import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App';
import { customerAPI, transactionAPI } from '../services/api';

const Dashboard = () => {
  const { user, updateUser } = React.useContext(UserContext);
  const navigate = useNavigate();
  
  const [balance, setBalance] = useState(null);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDashboardData();
  }, [user]);

  const fetchDashboardData = async () => {
    if (!user) {
      console.log('No user found, skipping dashboard data fetch');
      return;
    }
    
    console.log('Fetching dashboard data for user:', user.id);
    setLoading(true);
    setError(null);
    
    try {
      // Set initial balance from user object if available
      if (user.balance !== undefined) {
        setBalance(user.balance);
      }
      
      // Create a timeout promise
      const timeoutPromise = new Promise((_, reject) => 
        setTimeout(() => reject(new Error('Request timeout')), 10000)
      );
      
      // Fetch current balance with timeout
      console.log('Fetching balance...');
      const balancePromise = customerAPI.getBalance(user.id);
      const balanceData = await Promise.race([balancePromise, timeoutPromise]);
      console.log('Balance data received:', balanceData);
      setBalance(balanceData.balance);
      
      // Fetch recent transactions with timeout
      console.log('Fetching transactions...');
      const transactionsPromise = transactionAPI.getTransactions(user.id);
      const transactionsData = await Promise.race([transactionsPromise, timeoutPromise]);
      console.log('Transactions data received:', transactionsData);
      setRecentTransactions(transactionsData.slice(0, 5));
      
      // Update user object with latest balance
      updateUser({ ...user, balance: balanceData.balance });
      console.log('Dashboard data loaded successfully');
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      
      // If it's a timeout or network error, show a more user-friendly message
      if (error.message === 'Request timeout' || error.message === 'Network Error') {
        setError('Unable to connect to server. Please check if the backend is running on http://localhost:8080');
      } else {
        setError(error.error || error.message || 'Failed to load dashboard data');
      }
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleQuickAction = (action) => {
    navigate(`/${action}`);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card">
        <div className="error-message text-center">
          <h3>⚠️ Dashboard Error</h3>
          <p>{error}</p>
          <div className="mt-3">
            <button 
              onClick={fetchDashboardData} 
              className="btn btn-primary"
            >
              Retry
            </button>
            <button 
              onClick={() => {
                setError(null);
                setLoading(false);
                setBalance(user?.balance || 0);
                setRecentTransactions([]);
              }}
              className="btn btn-secondary ml-2"
            >
              Continue Offline
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Welcome Section */}
      <div className="card">
        <div className="card-header">
          <div>
            <h1 className="card-title">Welcome back, {user?.firstName}!</h1>
            <p className="card-subtitle">
              Here's an overview of your account activity
            </p>
          </div>
        </div>
      </div>

      {/* Balance Card */}
      <div className="card">
        <div className="balance-card">
          <div className="balance-label">Current Balance</div>
          <div className="balance-amount">
            {formatCurrency(balance || 0)}
          </div>
          <div className="balance-currency">USD</div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Quick Actions</h2>
        </div>
        <div className="quick-actions">
          <button
            className="quick-action-btn"
            onClick={() => handleQuickAction('deposit')}
          >
            <div className="quick-action-icon">💰</div>
            <div className="quick-action-label">Deposit Money</div>
          </button>
          <button
            className="quick-action-btn"
            onClick={() => handleQuickAction('withdraw')}
          >
            <div className="quick-action-icon">💸</div>
            <div className="quick-action-label">Withdraw Money</div>
          </button>
          <button
            className="quick-action-btn"
            onClick={() => handleQuickAction('passbook')}
          >
            <div className="quick-action-icon">📋</div>
            <div className="quick-action-label">View Passbook</div>
          </button>
        </div>
      </div>

      {/* Recent Transactions */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Recent Transactions</h2>
          <button
            className="btn btn-secondary"
            onClick={() => navigate('/passbook')}
          >
            View All
          </button>
        </div>
        
        {recentTransactions.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">📊</div>
            <div className="empty-state-title">No Transactions Yet</div>
            <div className="empty-state-description">
              Start by making your first deposit or withdrawal
            </div>
            <button
              className="btn btn-primary mt-3"
              onClick={() => handleQuickAction('deposit')}
            >
              Make First Deposit
            </button>
          </div>
        ) : (
          <div className="transaction-list">
            {recentTransactions.map((transaction) => (
              <div key={transaction.id} className="transaction-item">
                <div className="transaction-info">
                  <div className={`transaction-type ${transaction.type.toLowerCase()}`}>
                    {transaction.type === 'DEPOSIT' ? '💰 Deposit' : '💸 Withdrawal'}
                  </div>
                  <div className="transaction-description">
                    {transaction.description}
                  </div>
                  <div className="transaction-date">
                    {formatDate(transaction.timestamp)}
                  </div>
                </div>
                <div className={`transaction-amount ${
                  transaction.type === 'DEPOSIT' ? 'positive' : 'negative'
                }`}>
                  {transaction.type === 'DEPOSIT' ? '+' : '-'}
                  {formatCurrency(transaction.amount)}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Account Information */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Account Information</h2>
        </div>
        <div className="grid grid-2">
          <div>
            <h3 className="mb-2">Personal Details</h3>
            <p><strong>Name:</strong> {user?.firstName} {user?.lastName}</p>
            <p><strong>Username:</strong> {user?.username}</p>
            <p><strong>Email:</strong> {user?.email}</p>
            <p><strong>Phone:</strong> {user?.phoneNumber}</p>
          </div>
          <div>
            <h3 className="mb-2">Account Status</h3>
            <p><strong>Account ID:</strong> {user?.id}</p>
            <p><strong>Member Since:</strong> {formatDate(user?.id ? new Date().toISOString() : '')}</p>
            <p><strong>Status:</strong> <span className="text-success">Active</span></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
