import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App';
import { customerAPI, transactionAPI } from '../services/api';

const TransferModal = () => {
  const { user, updateUser } = React.useContext(UserContext);
  const navigate = useNavigate();

  const [formData, setFormData] = useState({ toUsername: '', amount: '', description: '' });
  const [recipient, setRecipient] = useState(null);
  const [currentBalance, setCurrentBalance] = useState(user?.balance || 0);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [searching, setSearching] = useState(false);

  useEffect(() => {
    const loadBalance = async () => {
      try {
        const res = await customerAPI.getBalance(user.id);
        setCurrentBalance(res.balance);
      } catch {}
    };
    if (user?.id) loadBalance();
  }, [user?.id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (name === 'toUsername') setRecipient(null);
    if (errors[name]) setErrors(prev => ({ ...prev, [name]: '' }));
  };

  const searchRecipient = async () => {
    if (!formData.toUsername.trim()) {
      setErrors(prev => ({ ...prev, toUsername: 'Enter a username' }));
      return;
    }
    if (formData.toUsername.trim() === user?.username) {
      setErrors(prev => ({ ...prev, toUsername: 'Cannot transfer to yourself' }));
      return;
    }
    setSearching(true);
    try {
      const data = await customerAPI.getByUsername(formData.toUsername.trim());
      setRecipient(data);
    } catch (err) {
      setRecipient(null);
      setErrors(prev => ({ ...prev, toUsername: 'Username not found' }));
    } finally {
      setSearching(false);
    }
  };

  const validate = () => {
    const newErrors = {};
    const amt = parseFloat(formData.amount);
    if (!recipient) newErrors.toUsername = 'Search and select a valid recipient';
    if (isNaN(amt) || amt <= 0) newErrors.amount = 'Enter a valid amount > 0';
    else if (amt > parseFloat(currentBalance || 0)) newErrors.amount = `Insufficient balance. Available: $${parseFloat(currentBalance || 0).toFixed(2)}`;
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;
    setLoading(true);
    try {
      const amt = parseFloat(formData.amount);
      const res = await transactionAPI.transfer(user.id, formData.toUsername.trim(), amt, formData.description || 'Money transfer');
      updateUser({ ...user, balance: res.fromCustomerNewBalance });
      alert(`Transferred $${amt.toFixed(2)} to ${recipient.firstName} ${recipient.lastName} (@${recipient.username})`);
      navigate('/dashboard');
    } catch (err) {
      setErrors({ general: err.error || err.message || 'Transfer failed' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2 className="modal-title">Transfer Money</h2>
          <button onClick={() => navigate('/dashboard')} className="close-btn">×</button>
        </div>
        <form onSubmit={handleSubmit}>
          {errors.general && <div className="error-message mb-3 text-center">{errors.general}</div>}

          <div className="form-group">
            <label className="form-label">Your Current Balance</label>
            <div className="balance-display">${parseFloat(currentBalance || 0).toFixed(2)}</div>
          </div>

          <div className="form-group">
            <label className="form-label">Recipient Username</label>
            <div className="flex gap-2">
              <input name="toUsername" value={formData.toUsername} onChange={handleChange} className={`form-input flex-1 ${errors.toUsername ? 'error' : ''}`} placeholder="Enter recipient username" />
              <button type="button" onClick={searchRecipient} className="btn btn-secondary" disabled={searching}>{searching ? 'Searching...' : 'Search'}</button>
            </div>
            {errors.toUsername && <div className="error-message">{errors.toUsername}</div>}
          </div>

          {recipient && (
            <div className="form-group">
              <div className="recipient-info">
                <h4>Recipient</h4>
                <p><strong>Name:</strong> {recipient.firstName} {recipient.lastName}</p>
                <p><strong>Username:</strong> {recipient.username}</p>
                <p><strong>Email:</strong> {recipient.email}</p>
              </div>
            </div>
          )}

          <div className="form-group">
            <label className="form-label">Amount</label>
            <input type="number" name="amount" value={formData.amount} onChange={handleChange} className={`form-input ${errors.amount ? 'error' : ''}`} step="0.01" min="0.01" placeholder="0.00" />
            {errors.amount && <div className="error-message">{errors.amount}</div>}
          </div>

          <div className="form-group">
            <label className="form-label">Description (optional)</label>
            <input name="description" value={formData.description} onChange={handleChange} className="form-input" placeholder="Add a note" />
          </div>

          <div className="flex justify-between mt-4">
            <button type="button" className="btn btn-secondary" onClick={() => navigate('/dashboard')}>Cancel</button>
            <button type="submit" className="btn btn-primary" disabled={loading || !recipient}>{loading ? 'Processing...' : 'Transfer Money'}</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TransferModal;
