import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App';
import { transactionAPI } from '../services/api';

const DepositModal = () => {
  const { user, updateUser } = React.useContext(UserContext);
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({
    amount: '',
    description: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.amount.trim()) {
      newErrors.amount = 'Amount is required';
    } else {
      const amount = parseFloat(formData.amount);
      if (isNaN(amount) || amount <= 0) {
        newErrors.amount = 'Please enter a valid amount greater than 0';
      }
      if (amount > 10000) {
        newErrors.amount = 'Maximum deposit amount is $10,000';
      }
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    
    try {
      const amount = parseFloat(formData.amount);
      const transaction = await transactionAPI.deposit(
        user.id,
        amount,
        formData.description || 'Money deposited'
      );
      
      // Update user balance
      const updatedUser = { ...user, balance: transaction.balanceAfterTransaction };
      updateUser(updatedUser);
      
      // Navigate back to dashboard
      navigate('/dashboard');
    } catch (error) {
      setErrors({
        general: error.error || 'Deposit failed. Please try again.'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    navigate('/dashboard');
  };

  const presetAmounts = [50, 100, 250, 500, 1000];

  const handlePresetAmount = (amount) => {
    setFormData(prev => ({
      ...prev,
      amount: amount.toString()
    }));
    setErrors(prev => ({
      ...prev,
      amount: ''
    }));
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2 className="modal-title">Deposit Money</h2>
          <button onClick={handleClose} className="close-btn">Ã—</button>
        </div>

        <form onSubmit={handleSubmit}>
          {errors.general && (
            <div className="error-message mb-3 text-center">
              {errors.general}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="amount" className="form-label">
              Amount to Deposit
            </label>
            <input
              type="number"
              id="amount"
              name="amount"
              value={formData.amount}
              onChange={handleChange}
              className={`form-input ${errors.amount ? 'error' : ''}`}
              placeholder="0.00"
              step="0.01"
              min="0.01"
              max="10000"
            />
            {errors.amount && (
              <div className="error-message">{errors.amount}</div>
            )}
          </div>

          <div className="form-group">
            <label className="form-label">Quick Amounts</label>
            <div className="quick-actions">
              {presetAmounts.map((amount) => (
                <button
                  key={amount}
                  type="button"
                  className="quick-action-btn"
                  onClick={() => handlePresetAmount(amount)}
                >
                  ${amount}
                </button>
              ))}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="description" className="form-label">
              Description (Optional)
            </label>
            <input
              type="text"
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter a description for this deposit"
            />
          </div>

          <div className="flex justify-between mt-4">
            <button
              type="button"
              onClick={handleClose}
              className="btn btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-success"
              disabled={loading}
            >
              {loading ? (
                <>
                  <div className="spinner" style={{ width: '20px', height: '20px' }}></div>
                  Processing...
                </>
              ) : (
                'Deposit Money'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DepositModal;
