import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App';
import { customerAPI, transactionAPI } from '../services/api';

const Dashboard = () => {
  const { user, updateUser } = React.useContext(UserContext);
  const navigate = useNavigate();

  const [balance, setBalance] = useState(user?.balance || 0);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [dataLoaded, setDataLoaded] = useState(false);

  useEffect(() => {
    // Initialize with user data immediately
    if (user) {
      setBalance(user.balance || 0);
      setDataLoaded(true);
      // Try to fetch fresh data in background once per user id change
      fetchDashboardData();
    }
  }, [user?.id]);

  const fetchDashboardData = async () => {
    if (!user) return;

    setLoading(true);
    setError(null);

    try {
      // Fetch current balance
      const balanceData = await customerAPI.getBalance(user.id);
      setBalance(balanceData.balance);

      // Fetch recent transactions
      console.log('Dashboard: Fetching transactions for user ID:', user.id);
      let transactionsData = await transactionAPI.getTransactions(user.id);
      console.log('Dashboard: Raw transactions response:', transactionsData);
      console.log('Dashboard: Transactions type:', typeof transactionsData);
      console.log('Dashboard: Is array:', Array.isArray(transactionsData));

      // If we somehow received an object (e.g., passbook wrapper), try to unwrap
      if (transactionsData && !Array.isArray(transactionsData) && transactionsData.transactions) {
        console.log('Dashboard: Unwrapping transactions from object');
        transactionsData = transactionsData.transactions;
      }

      // If still empty, try alternate endpoint (passbook) as fallback
      if (!transactionsData || transactionsData.length === 0) {
        console.log('Dashboard: No transactions found, trying passbook endpoint');
        try {
          const passbook = await transactionAPI.getPassbook(user.id);
          console.log('Dashboard: Passbook response:', passbook);
          transactionsData = passbook?.transactions || [];
        } catch (passbookError) {
          console.log('Dashboard: Passbook fetch failed:', passbookError);
        }
      }

      console.log('Dashboard: Final transactions data:', transactionsData);
      console.log('Dashboard: Final transactions length:', transactionsData?.length || 0);

      // Ensure we have a proper array and set it
      const transactionsArray = Array.isArray(transactionsData) ? transactionsData : [];
      console.log('Dashboard: Setting transactions array:', transactionsArray);
      setRecentTransactions(transactionsArray.slice(0, 5));

      setDataLoaded(true);
    } catch (error) {
      console.error('Dashboard data fetch error:', error);
      // Don't show error, just use existing data
      setDataLoaded(true);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleQuickAction = (action) => {
    navigate(`/${action}`);
  };

  // Show loading only if we have no user data at all
  if (!user) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading user data...</p>
      </div>
    );
  }

  return (
    <div>
      {/* Welcome Section */}
      <div className="card">
        <div className="card-header">
          <div>
            <h1 className="card-title">Welcome back, {user?.firstName}!</h1>
            <p className="card-subtitle">
              Here's an overview of your account activity
              {loading && <span className="ml-2">üîÑ Refreshing...</span>}
              {!loading && !dataLoaded && <span className="ml-2">‚ö†Ô∏è Using cached data</span>}
            </p>
          </div>
          <div className="flex gap-2">
            <button 
              onClick={fetchDashboardData}
              className="btn btn-secondary"
              disabled={loading}
            >
              {loading ? 'Refreshing...' : 'Refresh'}
            </button>
            <button 
              onClick={async () => {
                console.log('Manual test: Fetching transactions for user:', user.id);
                try {
                  const result = await transactionAPI.getTransactions(user.id);
                  console.log('Manual test result:', result);
                  alert(`Transactions found: ${result?.length || 0}. Check console for details.`);
                } catch (error) {
                  console.error('Manual test error:', error);
                  alert(`Error: ${error.message || error}`);
                }
              }}
              className="btn btn-primary"
            >
              Test API
            </button>
            <button 
              onClick={async () => {
                console.log('Direct test: Testing both endpoints for user:', user.id);
                try {
                  // Test regular endpoint
                  console.log('Testing /transactions/customer/' + user.id);
                  const regularResult = await transactionAPI.getTransactions(user.id);
                  console.log('Regular endpoint result:', regularResult);

                  // Test passbook endpoint
                  console.log('Testing /transactions/customer/' + user.id + '/passbook');
                  const passbookResult = await transactionAPI.getPassbook(user.id);
                  console.log('Passbook endpoint result:', passbookResult);

                  alert(`Regular: ${regularResult?.length || 0} transactions. Passbook: ${passbookResult?.transactions?.length || 0} transactions. Check console.`);
                } catch (error) {
                  console.error('Direct test error:', error);
                  alert(`Error: ${error.message || error}`);
                }
              }}
              className="btn btn-secondary"
            >
              Test Both
            </button>
          </div>
        </div>
      </div>

      {/* Balance Card */}
      <div className="card">
        <div className="balance-card">
          <div className="balance-label">Current Balance</div>
          <div className="balance-amount">
            {formatCurrency(balance || 0)}
          </div>
          <div className="balance-currency">USD</div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Quick Actions</h2>
        </div>
        <div className="quick-actions">
          <button
            className="quick-action-btn"
            onClick={() => handleQuickAction('deposit')}
          >
            <div className="quick-action-icon">üí∞</div>
            <div className="quick-action-label">Deposit Money</div>
          </button>
          <button
            className="quick-action-btn"
            onClick={() => handleQuickAction('withdraw')}
          >
            <div className="quick-action-icon">üí∏</div>
            <div className="quick-action-label">Withdraw Money</div>
          </button>
          <button
            className="quick-action-btn"
            onClick={() => handleQuickAction('passbook')}
          >
            <div className="quick-action-icon">üìã</div>
            <div className="quick-action-label">View Passbook</div>
          </button>
        </div>
      </div>

      {/* Recent Transactions */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Recent Transactions</h2>
          <button
            className="btn btn-secondary"
            onClick={() => navigate('/passbook')}
          >
            View All
          </button>
        </div>

        {!recentTransactions || recentTransactions.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">üìä</div>
            <div className="empty-state-title">No Recent Transactions</div>
            <div className="empty-state-description">
              {loading ? 'Loading transactions...' : 'Start by making your first deposit or withdrawal'}
            </div>
            <div className="mt-2 text-sm text-gray-500">
              Debug: Transactions array length: {recentTransactions?.length || 0}
            </div>
            {!loading && (
              <button
                className="btn btn-primary mt-3"
                onClick={() => handleQuickAction('deposit')}
              >
                Make First Deposit
              </button>
            )}
          </div>
        ) : (
          <div className="transaction-list">
            {recentTransactions.map((transaction) => (
              <div key={transaction.id} className="transaction-item">
                <div className="transaction-info">
                  <div className={`transaction-type ${transaction.type.toLowerCase()}`}>
                    {transaction.type === 'DEPOSIT' ? 'üí∞ Deposit' : 'üí∏ Withdrawal'}
                  </div>
                  <div className="transaction-description">
                    {transaction.description}
                  </div>
                  <div className="transaction-date">
                    {formatDate(transaction.timestamp)}
                  </div>
                </div>
                <div className={`transaction-amount ${
                  transaction.type === 'DEPOSIT' ? 'positive' : 'negative'
                }`}>
                  {transaction.type === 'DEPOSIT' ? '+' : '-'}
                  {formatCurrency(transaction.amount)}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Debug Information */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Debug Information</h2>
        </div>
        <div className="grid grid-2">
          <div>
            <h3 className="mb-2">Data Status</h3>
            <p><strong>Loading:</strong> {loading ? 'Yes' : 'No'}</p>
            <p><strong>Data Loaded:</strong> {dataLoaded ? 'Yes' : 'No'}</p>
            <p><strong>Transactions Count:</strong> {recentTransactions?.length || 0}</p>
            <p><strong>Balance:</strong> {balance}</p>
            <p><strong>User ID:</strong> {user?.id}</p>
            <p><strong>Backend URL:</strong> http://localhost:8080/api/transactions/customer/{user?.id}</p>
          </div>
          <div>
            <h3 className="mb-2">User Data</h3>
            <p><strong>User ID:</strong> {user?.id}</p>
            <p><strong>Username:</strong> {user?.username}</p>
            <p><strong>User Balance:</strong> {user?.balance}</p>
            <p><strong>State Balance:</strong> {balance}</p>
          </div>
        </div>
      </div>

      {/* Account Information */}
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Account Information</h2>
        </div>
        <div className="grid grid-2">
          <div>
            <h3 className="mb-2">Personal Details</h3>
            <p><strong>Name:</strong> {user?.firstName} {user?.lastName}</p>
            <p><strong>Username:</strong> {user?.username}</p>
            <p><strong>Email:</strong> {user?.email}</p>
            <p><strong>Phone:</strong> {user?.phoneNumber}</p>
          </div>
          <div>
            <h3 className="mb-2">Account Status</h3>
            <p><strong>Account ID:</strong> {user?.id}</p>
            <p><strong>Member Since:</strong> {formatDate(user?.id ? new Date().toISOString() : '')}</p>
            <p><strong>Status:</strong> <span className="text-success">Active</span></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
